import react, { useRef, useState, useEffect } from "react";

import classes from "./Auth.module.css";
import { useDispatch } from "react-redux";
import { authActions } from "../store/auth";

const Auth = () => {
  const dispatch = useDispatch();

  const email = useRef();
  const password = useRef();

  const [isActive, setIsActive] = useState(true);

  const [validEmail, setIsValidEmail] = useState(false);
  const [validPassword, setIsValidPassword] = useState(false);

  useEffect(() => {
    validEmail && validPassword !== "" ? setIsActive(false) : setIsActive(true);
  }, [validEmail, validPassword]);

  const onEmailChangeHandler = () => {
    email.current.value !== "" ? setIsValidEmail(true) : setIsValidEmail(false);
  };

  const onPasswordChangeHandler = () => {
    password.current.value !== ""
      ? setIsValidPassword(true)
      : setIsValidPassword(false);
  };

  const loginHandler = (event) => {
    event.preventDefault();

    dispatch(authActions.login());
  };

  return (
    <main className={classes.auth}>
      <section>
        <form onSubmit={loginHandler}>
          <div className={classes.control}>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              ref={email}
              onChange={onEmailChangeHandler}
            />
          </div>
          <div className={classes.control}>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              ref={password}
              onChange={onPasswordChangeHandler}
            />
          </div>
          <button disabled={isActive ? "isNotActive" : "isActive"} className={classes.login}>
            Login
          </button>
        </form>
      </section>
    </main>
  );
};

export default Auth;
