import React from "react";
import useInput from "../hooks/use-input";
import classes from "./Checkout.module.css";

// const isFiveChars = (value) => value.trim().length === 5;

const Checkout = (props) => {
  const {
    inputName: enteredName,
    inputHasError: thisNameHasError,
    inputIsTouched: onNameBlurHandler,
    inputNameChanged: nameChangeHandler,
  } = useInput((value) => value.trim() !== "");
  
  const {
    inputName: enteredStreet,
    inputHasError: thisStreetHasError,
    inputIsTouched: onStreetBlurHandler,
    inputNameChanged: streetChangeHandler,
  } = useInput((value) => value.trim() !== "");

  const {
    inputName: enteredPostal,
    inputHasError: thisPostalHasError,
    inputIsTouched: onPostalBlurHandler,
    inputNameChanged: postalChangeHandler,
  } = useInput((value) => value.length === 5);

  //   const formIsValid =
  //     enteredNameIsValid &&
  //     enteredStreetIsValid &&
  //     enteredCityIsValid &&
  //     enteredPostalCodeIsValid;

  //   if (!formIsValid) {
  //     return;
  //   }

  //   props.onConfirm({
  //     name: enteredName,
  //     street: enteredStreet,
  //     city: enteredCity,
  //     postalCode: enteredPostalCode,
  //   })


  const nameControlClasses = `${classes.control} ${
    !thisNameHasError ? "" : classes.invalid
  }`;


  const streetControlClasses = `${classes.control} ${
    !thisStreetHasError ? "" : classes.invalid
  }`;
  // const postalCodeControlClasses = `${classes.control} ${
  //   formInputsValidity.postalCode ? '' : classes.invalid
  // }`;
  // const cityControlClasses = `${classes.control} ${
  //   formInputsValidity.city ? '' : classes.invalid
  // }`;
  const onSubmitHandler = () => {
    props.onConfirm();
  };

  return (
    <form className={classes.form} onSubmit={onSubmitHandler}>
      <div className={nameControlClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={streetChangeHandler}
          onBlur={onNameBlurHandler}
          value={enteredName}
        />
        {/* {!formInputsValidity.name && <p>Please enter a valid name!</p>} */}
      </div>
      <div className={streetControlClasses}>
        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          onChange={nameChangeHandler}
          onBlur={onStreetBlurHandler}
          value={enteredStreet}
        />
        {/* {!formInputsValidity.street && <p>Please enter a valid street!</p>} */}
      </div>
      <div className={'form-control'}>
        <label htmlFor="postal">Postal Code</label>
        <input
          type="text"
          id="postal"
          onChange={postalChangeHandler}
          onBlur={onPostalBlurHandler}
          value={enteredPostal}
        />
        {/* {!formInputsValidity.postalCode && (
          <p>Please enter a valid postal code (5 characters long)!</p>
        )} */}
      </div>
      <div className={'form-control'}>
        <label htmlFor="city">City</label>
        <input
          type="text"
          id="city"
        />
        {/* {!formInputsValidity.city && <p>Please enter a valid city!</p>} */}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};
export default Checkout;
