import react, { useReducer } from "react";

const inputReducerHandler = (action) => {
  if (action.type === "INPUT") {
    return { name: action.value };
  }
};

const useInput = (validateInput) => {
  const initialState = {
    name: "",
    street: "",
    city: "",
    postalCode: "",
  };

  const [initState, dispatch] = useReducer(inputReducerHandler, initialState);

  const isValidity = validateInput(initState.name);
  console.log(isValidity);

  const inputNameChanged = (event) => {
    dispatch({ type: "INPUT", value: event.target.value });
    console.log(event.target.value);
  };

  //   const confirmHandler = (event) => {
  //     event.preventDefault();

  //     const enteredNameIsValid = !isEmpty(enteredName);
  //     const enteredStreetIsValid = !isEmpty(enteredStreet);
  //     const enteredCityIsValid = !isEmpty(enteredCity);
  //     const enteredPostalCodeIsValid = isFiveChars(enteredPostalCode);

  //     setFormInputsValidity({
  //       name: enteredNameIsValid,
  //       street: enteredStreetIsValid,
  //       city: enteredCityIsValid,
  //       postalCode: enteredPostalCodeIsValid,
  //     });

  return {
    value: initState.name,
    isValidity,
    inputNameChanged,
  };
};

export default useInput;
