import react, { useReducer } from "react";

const inputReducerHandler = (action) => {
  if (action.type === "INPUT") {
    return { name: action.value };
  }
};

const useInput = (validateInput) => {
  const initialState = {
    word: '',
  };
  
  const [initState, dispatch] = useReducer(inputReducerHandler, initialState);
  
  const isValidity = validateInput(initState.name);
  console.log(initState)

  const inputNameChanged = (event) => {
    dispatch({ type: "INPUT", value: event.target.value });
  };

  return {
    inputName: initState.word,
    isVerificated: isValidity,
    inputNameChanged,
  };
};

export default useInput;
