import { useReducer } from "react";

const initialState = {
  word: "",
  test: false
};

const inputReducerHandler = (state, action) => {
  if (action.type === "INPUT") {
    return { word: action.value, test: state.test };
  }
};

const useInput = (validateInput) => {
  const [initState, dispatch] = useReducer(
  inputReducerHandler, 
  initialState
  );
  
  const isValidity = validateInput(initialState.word);

  const inputNameChanged = (event) => {
    dispatch({ type: "INPUT", value: event.target.value });
  };

  return {
    inputName: initState.word,
    isVerificated: isValidity,
    inputNameChanged,
  };
};

export default useInput;
