import { useState, useEffect } from "react";

const BasicForm = (props) => {


  const [nameIsValid, setNameIsValid] = useState(false);
  const [lastNameIsValid, setLastNameIsValid] = useState(false);
  const [emailIsValid, setEmailIsValid] = useState(false);

  const [nameInputClass, setNameInputClass] = useState("form-control");
  const [lastNameInputClass, setLastNameInputClass] = useState("form-control");
  const [emailInputClass, setEmailInputClass] = useState("form-control");

  const nameChangeInputHandler = (event) => {
    setEnteredName(event.target.value);
    setNameInputClass("form-control");


  };
  const lastNameChangeInputHandler = (event) => {
    setEnteredLastName(event.target.value);
    setLastNameInputClass("form-control");

  };
  const emailChangeInputHandler = (event) => {
    setEnteredEmail(event.target.value);
    setEmailInputClass("form-control");

  };

  const validateNameInputHandler = () => {
    if (enteredName.trim().length > 0) {
      setNameIsValid(true);
      setNameInputClass("form-control");
    } else {
      setNameIsValid(false);
      setNameInputClass("form-control invalid");
    }
  };
  const validateLastNameInputHandler = () => {
    if (enteredLastName.trim().length > 0) {
      setLastNameIsValid(true);
      setLastNameInputClass("form-control");
    } else {
      setLastNameIsValid(false);
      setLastNameInputClass("form-control invalid");
    }
  };
  const validateEmailInputHandler = () => {
    if (enteredEmail.includes("@")) {
      setEmailIsValid(true);
      setEmailInputClass("form-control");
    } else {
      setEmailIsValid(false);
      setEmailInputClass("form-control invalid");
    }
  };

  const submitHandler = () => {
    setEnteredName("");
    setEnteredLastName("");
    setEnteredEmail("");
    // setIsValid(false);
  };

  let formIsValid = false;

  if (nameIsValid && emailIsValid && lastNameIsValid) {
    formIsValid = true;
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={nameInputClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={nameChangeInputHandler}
            onBlur={validateNameInputHandler}
            value={enteredName}
          />
        </div>
        <div className={lastNameInputClass}>
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            onChange={lastNameChangeInputHandler}
            onBlur={validateLastNameInputHandler}
            value={enteredLastName}
          />
        </div>
      </div>
      <div className={emailInputClass}>
        <label htmlFor="email">E-Mail Address</label>
        <input
          type="email"
          id="email"
          onChange={emailChangeInputHandler}
          onBlur={validateEmailInputHandler}
          value={enteredEmail}
        />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
