import useInput from "../components/hooks/use-input2";

const BasicForm = (props) => {
  const {
    value: enteredName,
    applyChanges: styleDefinition,
    valueIsValidated: isValid,
    inputChangeHandler: nameChangeInputHandler,
    inputIsTouched: inputBlurHandler,
  } = useInput((value) => value.trim() !== "");

  return (
    <form >
      <div className="control-group">
        <div className={styleDefinition}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={nameChangeInputHandler}
            onBlur={inputBlurHandler}
            value={enteredName}
          />
        </div>
      </div>
      <div className="form-actions">
        <button disabled={!isValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
