import react, { useState } from "react";

const useInput = (valueIsValid) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [applyChanges, setApplyChanges] = useState("form-control");

  const valueIsValidated = valueIsValid(enteredValue);
  const hasError = !valueIsValid

  const inputChangeHandler = (event) => {
    setEnteredValue(event.target.value);
    setApplyChanges("form-control")
  };

  const inputIsTouched = () => {
    valueIsValidated
      ? setApplyChanges("form-control")
      : setApplyChanges("form-control invalid");
  };

  const reset = () => {
    setEnteredValue('')
  }


  return {
    hasError,
    enteredValue,
    applyChanges,
    valueIsValidated,
    reset,
    inputIsTouched,
    inputChangeHandler,
  };
};

export default useInput;
