import React, { useState, useReducer } from "react";

const useInput = (valueIsValid) => {
  const initialInputState = {
    value: "",
    isTouched: false,
  };

  const inputReducerHandler = (state, action) => {
    if (action.type === "INPUT") {
      initialInputState = { name: action.value, isTouched: state.isTouched };
    }
    if (action.type === "BLUR") {
      initialInputState = { name: state.name, isTouched: action.value };
    }
  };

  const [initState, dispatch] = useReducer(
    inputReducerHandler,
    initialInputState
  );

  const valueIsValidated = valueIsValid(initState.value);
  const hasError = !valueIsValidated && initState.isTouched;

  const inputChangeHandler = (event) => {
    dispatch({ type: "INPUT", value: event.target.value });
  };

  const inputIsTouched = () => {
    dispatch({ type: "BLUR", value: true });
  };

  const reset = () => {};

  return {
    value: initState.value,
    isTouched: initState.isTouched,
    hasError,
    reset,
    inputIsTouched,
    inputChangeHandler,
  };
};

export default useInput;
