import react, { useState } from "react";

const useInput = (valueIsValid) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [applyChanges, setApplyChanges] = useState("");
  const valueIsValidated = valueIsValid(enteredValue);

  const inputChangeHandler = (event) => {
    setEnteredValue(event.target.value);
  };

  const inputIsTouched = () => {
    valueIsValidated ? setApplyChanges('form-control'): setApplyChanges('form-control');
  }

  const reset = () => {
    setEnteredValue("");
  };

  return {
    enteredValue,
    valueIsValidated,
    applyChanges,
    inputIsTouched,
    inputChangeHandler,
    reset,
  };
};

export default useInput;
