import React, { useState, useReducer } from "react";

const useInput = (valueIsValid) => {
  const [initialState, setObjState] = useState({
    name: "",
    isTouched: false,
  });

  const inputReducerHandler = (state, action) => {
    if (action.type === "INPUT") {
      setObjState({ name: action.value, isTouched: state.isTouched });
    }
    if (action.type === "BLUR") {
      console.log(state.name)
      setObjState({ name: state.name, isTouched: action.value });
     
    }
  };

  const [initState, dispatch] = useReducer(inputReducerHandler, initialState);

  const valueIsValidated = valueIsValid(initialState.name);
  const hasError = !valueIsValidated && initialState.isTouched;

  const inputChangeHandler = (event) => {
    dispatch({ type:"INPUT", value: event.target.value });
  };

  const inputIsTouched = () => {
    dispatch({ type:"BLUR", value: true });
  };

  const reset = () => {
  };

  return {
    value: initialState.value,
    isTouched: initialState.isTouched,
    hasError,
    reset,
    inputIsTouched,
    inputChangeHandler,
  };
};

export default useInput;
