import react, { useState, useReducer } from "react";

const useInput = (valueIsValid) => {
  const [initialState, setObjState] = useState({
    name: '',
    isTouched: false
  })

  const [enteredValue, setEnteredValue] = useState("");
  const [isTouched, setIsTouched] = useState(false);
  
  const valueIsValidated = valueIsValid(enteredValue);
  const hasError = !valueIsValidated && isTouched;

  const [initState, dispatch] = useReducer(initialState, inputReducerHandler)

  const inputReducerHandler = (state, action) => {
    if (action.type === 'INPUT') {
      setObjState({name: action.name, isTouched: state.isTouched})
    }
  }

  const inputChangeHandler = (event) => {
    dispatch({type: 'INPUT', value: event.target.value})
  };

  const inputIsTouched = () => {
    setIsTouched(true);
  };

  const reset = () => {
    setEnteredValue("");
    setIsTouched(false);
  };

  return {
    hasError,
    isTouched,
    enteredValue,
    valueIsValidated,
    reset,
    inputIsTouched,
    inputChangeHandler,
  };
};

export default useInput;
