import { useState, useEffect } from "react";

const BasicForm = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredLastName, setEnteredLastName] = useState("");

  const [nameIsValid, setNameIsValid] = useState(false);
  const [lastNameIsValid, setLastNameIsValid] = useState(false);
  const [nameInputClass, setNameInputClass] = useState('form-control');
  // const nameInputClasses = isValid ? "form-control" : "form-control invalid";

  const [isValid, setIsValid] = useState(false);

  const nameChangeInputHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const lastNameChangeInputHandler = (event) => {
    setEnteredLastName(event.target.value);
  };

  const validateNameInputHandler = () => {
    enteredName.trim().length > 0 ? setNameIsValid(true) : setIsValid(false);
  };

  const validateLastNameInputHandler = () => {
    // enteredLastName.trim().length > 0
    //   ? setLastNameIsValid(true)
    //   : setIsValid(false);
    if (enteredLastName.trim().length > 0) {
      setLastNameIsValid(true)
      setNameInputClass('form-control')
    } else {
      setLastNameIsValid(false)
      setNameInputClass("form-control invalid")
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setEnteredName("");




  };

  useEffect(() => {
    // console.log(nameIsValid)
    // console.log(lastNameIsValid)
    nameIsValid && lastNameIsValid
      ? setIsValid(true)
      : setIsValid(false);
  }, [nameIsValid, lastNameIsValid]);

console.log(!isValid)

  // const lastNameInputClasses = !isValid ? "form-control " : "form-control invalid";

  // const emailInputClasses = emailInputHasError
  //   ? "form-control invalid"
  //   : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={nameInputClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={nameChangeInputHandler}
            onBlur={validateNameInputHandler}
            value={enteredName}
          />
        </div>
        <div className={nameInputClass}>
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            onChange={lastNameChangeInputHandler}
            onBlur={validateLastNameInputHandler}
            value={enteredLastName}
          />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="email">E-Mail Address</label>
        <input type="text" id="email" />
      </div>
      <div className="form-actions">
        <button disabled={!isValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
