import React, { useRef, useState } from 'react';

import classes from './AddMovie.module.css';

function AddMovie(props) {
  const [titleState, setTitleState] = useState()
  const [openingState, setOpeningState] = useState()
  const [releaseState, setReleaseState] = useState()

  const titleRef = useRef();
  const openingTextRef = useRef();
  const releaseDateRef = useRef();

  function submitHandler(event) {
    event.preventDefault();

    const movie = {
      title: titleRef.current.value,
      openingText: openingTextRef.current.value,
      releaseDate: releaseDateRef.current.value,
    };

    setTitleState()
    props.onAddMovie(movie);
  }

const titleStateHandler = () => {
  setTitleState(titleRef.current.value)
}

  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' ref={titleRef} value={titleState} onChange={titleStateHandler}/>
      </div>
      <div className={classes.control}>
        <label htmlFor='opening-text'>Opening Text</label>
        <textarea rows='5' id='opening-text' ref={openingTextRef} value={openingState}></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor='date'>Release Date</label>
        <input type='text' id='date' ref={releaseDateRef} value={releaseState}/>
      </div>
      <button>Add Movie</button>
    </form>
  );
}

export default AddMovie;
